import React from "react";
import { useSpring, animated } from "react-spring";

function Greeting() {
  const props = useSpring({
    to: { opacity: 1, marginLeft: 0 },
    from: { opacity: 0, marginLeft: -500 },
    config: { delay: 1000, duration: 2000 },
  });
  // const props2 = useSpring({
  //   to: { opacity: 1, marginBottom: 0 },
  //   from: { opacity: 0, marginBottom: -500 },
  //   config: { delay: 1000, duration: 3000 },
  // });
  return (
    <animated.div style={props}>
      <div id="home" className="flexContainer">
        <h1 className="greedingBox1">Ciao,</h1>

        <h2 className="greedingBox2" style={{ fontStyle: "italic" }}>
          I'm Giuseppe De Luca, <br></br> a software engineer <br></br>
          living in Brooklyn.
        </h2>
      </div>
    </animated.div>
  );
}

export default Greeting;
